export default (state, action) => {
    switch(action.type) {
        case "delete": 
            return {
                ...state,
                transactions: state.transactions.filter(transaction => transaction.id != action.data)
            }

        case "add":
            return {
                ...state,
                transactions: [action.data, ...state.transactions]
            }
        default:
            return state;
    }
}
